package no.steria.quizzical;

public class Question {

	private int id;
	private String text;

	public Question(int id, String text) {
		this.id = id;
		// TODO Auto-generated constructor stub
		this.text = text;
	}
	
	public int getId() {
		return id;
	}
	
	public String getText() {
		return text;
	}
	
	

}
